mapper.select:
select num,writer,subject,passwd,reg_date,ref,re_step,re_level,content,ip,readcount,r 
				from (select num,writer,subject,passwd,reg_date,ref,re_step,re_level,content,ip,readcount,rownum r 
				from (select *
				from otoboard order by otonum desc, re_step asc) order by otonum desc, re_step asc ) 
				where r >= #{startRow} and r <= #{endRow}
